/***** SETTINGS *****/
const TZ = 'Europe/London';
const SEND_WINDOW_MIN = 30;   // should be >= your schedule cadence
const DRY_RUN = false;        // true = only diagnostics, no messages

/***** HELPERS: TZ math (no external libs) *****/
function tzOffsetMs(tz, dateUtc) {
  const dtf = new Intl.DateTimeFormat('en-GB', {
    timeZone: tz, hour12: false,
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit'
  });
  const p = Object.fromEntries(dtf.formatToParts(dateUtc).map(({type, value}) => [type, value]));
  const asUtc = Date.UTC(+p.year, +p.month - 1, +p.day, +p.hour, +p.minute, +p.second);
  return asUtc - dateUtc.getTime();
}

function zonedTimeToUtc(y, m, d, hh, mm, ss, tz) {
  const guess = new Date(Date.UTC(y, m - 1, d, hh, mm, ss || 0));
  const off = tzOffsetMs(tz, guess);
  return guess.getTime() - off;
}

function parseLocalDateTimeToUTCms(str, tz) {
  if (!str) return null;
  const s = String(str).trim().replace('T', ' ');
  // yyyy-mm-dd hh:mm[:ss]
  const m = s.match(/^(\d{4})-(\d{2})-(\d{2})\s+(\d{2}):(\d{2})(?::(\d{2}))?$/);
  if (!m) return null;
  const [, Y, Mo, D, h, mi, se] = m.map(Number);
  return zonedTimeToUtc(Y, Mo, D, h, mi, se || 0, tz);
}

function localYMD(dateUtc, tz) {
  const dtf = new Intl.DateTimeFormat('en-GB', {
    timeZone: tz, hour12: false, year: 'numeric', month: '2-digit', day: '2-digit'
  });
  const p = Object.fromEntries(dtf.formatToParts(dateUtc).map(({type, value}) => [type, value]));
  return { y: +p.year, m: +p.month, d: +p.day };
}

function sameDay8amUtcMs(apptUtcMs, tz) {
  const { y, m, d } = localYMD(new Date(apptUtcMs), tz);
  return zonedTimeToUtc(y, m, d, 8, 0, 0, tz);
}

/***** HELPERS: phone cleaning *****/
function cleanPhone(x) {
  // Turn anything into digits-only string
  const digits = String(x ?? '').match(/\d+/g)?.join('') || '';
  console.log(digits);
  if (!digits) return ''; // will fail fast downstream if empty
  // If UK and number doesn't start with 44, add it (strip leading 0s)

  const withCc = digits;
  return withCc; // WhatsApp BC accepts digits-only (E.164 without '+')
}

/***** NOW & FORMATTING *****/
const nowMs = Date.now();
const windowStartMs = nowMs - SEND_WINDOW_MIN * 60 * 1000;
const windowEndMs   = nowMs + 60 * 1000; // small lookahead
const dateFmt = new Intl.DateTimeFormat('en-GB', {
  timeZone: TZ, dateStyle: 'medium', timeStyle: 'short', hour12: false
});
const isoUtc = (ms) => new Date(ms).toISOString();

/***** PROCESS ROWS *****/
const out = [];

for (const item of items) {
  const r = item.json;

  // Expected headers in your "October" sheet:
  // Name | Number | Request Date | Appointment | Service | Paid | Client ID
  const name     = r['Name'] || '';
  const rawPhone = r['Number'];
  const apptStr  = r['Appointment'];   // "YYYY-MM-DD HH:MM[:SS]" or "YYYY-MM-DDTHH:MM[:SS]"
  const service  = r['Service'] || '';
  const clientId = r['Client ID'] || '';

  if (!apptStr || !rawPhone) continue;

  const apptMs = parseLocalDateTimeToUTCms(apptStr, TZ);
  if (!apptMs) {
    if (DRY_RUN) out.push({ json: { diagnostics: true, reason: 'Bad date format', apptStr } });
    continue;
  }

  const to = cleanPhone(rawPhone);
  if (!to) {
    if (DRY_RUN) out.push({ json: { diagnostics: true, reason: 'Bad phone', rawPhone } });
    continue;
  }

  const sendAt08Ms = sameDay8amUtcMs(apptMs, TZ);
  const sendAt1hMs = apptMs - 60 * 60 * 1000;
  const inWindow = (t) => t >= windowStartMs && t <= windowEndMs;

  if (DRY_RUN) {
    out.push({
      json: {
        diagnostics: true,
        name, clientId, service, to,
        nowUtc: isoUtc(nowMs),
        apptLocal: dateFmt.format(new Date(apptMs)),
        sendAt08_local: dateFmt.format(new Date(sendAt08Ms)),
        sendAt08_utc: isoUtc(sendAt08Ms),
        sendAt1h_local: dateFmt.format(new Date(sendAt1hMs)),
        sendAt1h_utc: isoUtc(sendAt1hMs),
        windowStartUtc: isoUtc(windowStartMs),
        windowEndUtc: isoUtc(windowEndMs),
        shouldSend08: inWindow(sendAt08Ms),
        shouldSend1h: inWindow(sendAt1hMs),
      }
    });
    continue;
  }

  // 08:00 morning reminder
  if (inWindow(sendAt08Ms)) {
    out.push({
      json: {
        to, name, clientId, type: '08am',
        appointmentLocal: dateFmt.format(new Date(apptMs)),
        service,
        body: `Good morning ${name || ''}! Reminder: your appointment is today at ${dateFmt.format(new Date(apptMs))}. Service: ${service || '—'}. Phone Max on 07765 918355 if you need to re-schedule.`
      }
    });
  }

  // 1-hour-before reminder
  if (inWindow(sendAt1hMs)) {
    const timeOnlyFmt = new Intl.DateTimeFormat('en-GB', {
      timeZone: TZ,
      hour: '2-digit',
      minute: '2-digit',
      hour12: false
    });

    out.push({
      json: {
        to, name, clientId, type: '1h-before',
        appointmentLocal: dateFmt.format(new Date(apptMs)),
        service,
        body: `Hi ${name || ''}, quick reminder: your appointment is in 1 hour (${timeOnlyFmt.format(new Date(apptMs))}). See you soon!`
      }
    });
  }
}

return out;

// Expect TWO upstream items: one "config" (windowStart/windowEnd/slotMinutes/stepMinutes/...)
// and one "freebusy" (calendars: { <id>: { busy: [{start,end},...] } })

// 1) Find config + freebusy items
let cfg = null, fb = null;
for (const it of items) {
  const j = it.json || {};
  if (!cfg && j.windowStart && j.windowEnd) cfg = j;
  if (!fb && j.calendars) fb = j;
}
if (!cfg) throw new Error("Config item missing windowStart/windowEnd");
if (!fb || !fb.calendars) throw new Error("FreeBusy item missing calendars");

// helpers
function toMs(iso){ const t = Date.parse(iso); if (Number.isNaN(t)) throw new Error("Bad ISO: "+iso); return t; }
function localHM(iso, zone) {
  const s = new Intl.DateTimeFormat('en-GB', {
    hour: '2-digit', minute: '2-digit', hour12: false, timeZone: zone,
  }).format(new Date(iso));
  const [h,m] = s.split(':').map(Number);
  return h*60 + m;
}
function localWeekday(iso, zone) {
  return new Intl.DateTimeFormat('en-GB', { weekday: 'long', timeZone: zone })
    .format(new Date(iso)); // e.g. "Tuesday"
}

// Map business hours (minutes since midnight). null => closed.
function getBusinessWindow(weekday) {
  switch (weekday) {
    case 'Monday':    return null;                  // closed
    case 'Tuesday':
    case 'Wednesday': return { open: 10*60, close: 19*60 }; // 10:00–19:00
    case 'Thursday':
    case 'Friday':    return { open: 10*60, close: 20*60 }; // 10:00–20:00
    case 'Saturday':  return { open: 10*60, close: 18*60 }; // 10:00–18:00
    case 'Sunday':    return { open: 11*60, close: 16*60 }; // 11:00–16:00
    default:          return null;
  }
}

// 2) Read config
const tz          = cfg.timezone || 'Europe/London';
const windowStart = toMs(cfg.windowStart);
const windowEnd   = toMs(cfg.windowEnd);
const slotMs      = (Number(cfg.slotMinutes) || 30) * 60 * 1000;
const stepMs      = (Number(cfg.stepMinutes) || Number(cfg.slotMinutes) || 30) * 60 * 1000;
const bufBeforeMs = (Number(cfg.bufferBeforeMinutes) || 0) * 60 * 1000;
const bufAfterMs  = (Number(cfg.bufferAfterMinutes)  || 0) * 60 * 1000;

// 3) Collect busy intervals across all calendars
let intervals = [];
for (const [id, cal] of Object.entries(fb.calendars)) {
  if (!cal || (cal.errors && cal.errors.length)) continue;
  const busy = Array.isArray(cal.busy) ? cal.busy : [];
  for (const b of busy) {
    let s = toMs(b.start) - bufBeforeMs;
    let e = toMs(b.end)   + bufAfterMs;
    if (e <= windowStart || s >= windowEnd) continue;   // outside day window
    if (s < windowStart) s = windowStart;
    if (e > windowEnd)   e = windowEnd;
    intervals.push([s, e]);
  }
}
intervals.sort((a,b) => a[0] - b[0]);

// 4) Merge overlaps
const merged = [];
for (const iv of intervals) {
  if (!merged.length || iv[0] > merged[merged.length-1][1]) merged.push([iv[0], iv[1]]);
  else if (iv[1] > merged[merged.length-1][1]) merged[merged.length-1][1] = iv[1];
}

// 5) Free ranges = window - merged busy
const freeRanges = [];
let cursor = windowStart;
for (const [s,e] of merged) {
  if (cursor < s) freeRanges.push([cursor, s]);
  if (e > cursor) cursor = e;
}
if (cursor < windowEnd) freeRanges.push([cursor, windowEnd]);

// 6) Slice free ranges into slots (UTC) — enforce "no past" and ">= now + 1h"
function ceilToStep(t){ const r = (t - windowStart) % stepMs; return r === 0 ? t : t + (stepMs - r); }
const nowMs = Date.now();
const minStart = Math.max(windowStart, nowMs + 60 * 60 * 1000); // >= current time + 1h

let slots = [];
for (const [fs,fe] of freeRanges) {
  const effectiveStart = Math.max(fs, minStart);
  if (effectiveStart >= fe) continue;

  let t = ceilToStep(effectiveStart);
  while (t + slotMs <= fe) {
    slots.push({
      startZ: new Date(t).toISOString(),
      endZ:   new Date(t + slotMs).toISOString()
    });
    t += stepMs;
  }
}

// 7) FILTER to business hours for the day (in tz)
const weekday = localWeekday(cfg.windowStart, tz);
const biz = getBusinessWindow(weekday);

// If closed, wipe slots.
if (!biz) slots = [];
else {
  slots = slots.filter(s => {
    const startHM = localHM(s.startZ, tz);
    const endHM   = localHM(s.endZ,   tz);
    return startHM >= biz.open && endHM <= biz.close;
  });
}

// 8) Output (plus some debug)
const toZ = ([s,e]) => ({ startZ: new Date(s).toISOString(), endZ: new Date(e).toISOString() });

return [{
  json: {
    slots,
    debug: {
      timezone: tz,
      weekday,
      businessWindowMinutes: biz || null,
      windowStartZ: new Date(windowStart).toISOString(),
      windowEndZ:   new Date(windowEnd).toISOString(),
      mergedBusy:   merged.map(toZ),
      freeRanges:   freeRanges.map(toZ),
      minStartZ:    new Date(minStart).toISOString(),
    }
  }
}];

// Input expected on $json:
// { slots: [ {startZ:"..." , endZ:"..."} , ... ], timezone: "Europe/London" }

const slots = Array.isArray($json.slots) ? $json.slots.slice() : [];
const tz = $json.timezone || 'Europe/London';

// If no slots, say unavailable
if (!slots.length) {
  return [{ json: { available: false, reason: 'no_slots_for_day' } }];
}

// Sort by start time (UTC)
slots.sort((a, b) => new Date(a.startZ) - new Date(b.startZ));

// Helper: local hour in given zone
function localHour(iso, zone) {
  return Number(
    new Intl.DateTimeFormat('en-GB', {
      hour: '2-digit',
      hour12: false,
      timeZone: zone,
    }).format(new Date(iso))
  );
}

// Helper: build local ISO like 2025-09-01T09:30:00 (no offset; use with timeZone later)
function toLocalISO(iso, zone) {
  const d = new Date(iso);
  const datePart = new Intl.DateTimeFormat('en-CA', { // en-CA => YYYY-MM-DD
    timeZone: zone, year: 'numeric', month: '2-digit', day: '2-digit',
  }).format(d);
  const timePart = new Intl.DateTimeFormat('en-GB', {
    timeZone: zone, hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: false,
  }).format(d);
  return `${datePart}T${timePart}`;
}

// Split into AM (<12) and PM (>=12) by local start time for counts only
let amCount = 0, pmCount = 0;
for (const s of slots) {
  const h = localHour(s.startZ, tz);
  if (h < 12) amCount++; else pmCount++;
}

// Use **all** slots, enriched with local representations
const proposals = slots.map(s => ({
  ...s,
  startLocal: toLocalISO(s.startZ, tz),
  endLocal:   toLocalISO(s.endZ,   tz),
  timezone:   tz,
}));

return [{
  json: {
    available: true,
    selectedSlots: proposals,             // ALL available slots
    counts: { am: amCount, pm: pmCount, total: slots.length }
  }
}];
